import React from 'react';
import '../../styles/Form.css';
import Alert from '../../Alert';

const styles = {
  div: {
    margin: "20px",
    padding: "10px",
  },
  headingStyle: {
    fontSize: '100px',
  },
  textField: {
    height: "100px",
  },
};



export default function Contact() {
    // Here we set two state variables for firstName and lastName using `useState`
    const [userName, setName] = useState('');
    const handleInputChange = (e) => {
      // Getting the value and name of the input which triggered the change
      const { name, value } = e.target;

      // Ternary statement that will call either setName or setEmail based on what field the user is typing in
      return name === 'userName' ? setName(value) : setEmail(value);
    };

    const handleFormSubmit = (e) => {
      // Preventing the default behavior of the form submit (which is to refresh the page)
      e.preventDefault();

      // Alert the user their first and last name, clear the inputs
      alert(`Hello ${firstName} ${lastName}`);
      setFirstName('');
      setLastName('');
    };
  return (
    <div style={styles.div}>
      <h1 style={styles.headingStyle}>contact</h1>
      <form class="form-floating needs-validation" novalidate>
        <div class="form-floating mb-3">
          <input type="text" class="form-control" id="floatingInput" placeholder="name" onChange={handleInputChange} required></input>
          <label for="floatingInput">name</label>
          <div class="invalid-feedback">
            You must provide your name
          </div>
        </div>
        <div class="form-floating mb-3">
          <input type="email" class="form-control" id="floatingInputInvalid" placeholder="name@example.com" onChange={handleInputChange} required></input>
          <label for="floatingInput">email address</label>
        </div>
        <div class="form-floating">
          <textarea class="form-control" placeholder="Leave a comment here" id="floatingTextarea2" style={styles.textField} onChange={handleInputChange} required></textarea>
          <label for="floatingTextarea2">message</label>
        </div>
        <div class="m-3">
          <button class="btn" type="submit" onClick={handleFormSubmit} style={styles.submitBtn}>submit</button>
        </div>
      </form>
    </div>
  );
}
