import React, { state, Component } from 'react';
import '../../styles/Form.css';

const styles = {
  div: {
    margin: "20px",
    padding: "10px",
  },
  headingStyle: {
    fontSize: '100px',
  },
  textField: {
    height: "100px",
  },
};

export default function Contact() {
  class Contact extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        name: '',
        nameError: false,
        contact: '',
        email: '',
        emailError: false,
        emailError2: false,
        message: '',
        messageError: false,
        formValid: false
      };
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
      this.handleBlur = this.handleBlur.bind(this);
    };
    isValidEmail = (email) => {
      return /^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/.test(email);
    }
  };

  const handleBlur = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    this.setState({ [name]: value });
    if (value.length <= 0 && (name == 'name')) {
      this.setState({ nameError: true });
    } else {
      this.setState({ nameError: false });
    }
    if (value.length <= 0 || (name == 'email')) {
      this.setState({ emailError: true });
    } else {
      this.setState({ emailError: false });
      if (this.isValidEmail(value)) {
        this.setState({ emailError: false });
      } else {
        this.setState({ emailError: true });
      }
    }
  }
  const handleInputChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }
  // Getting the value and name of the input which triggered the change
  const handleFormSubmit = (e) => {
    const { name, email, message, nameError, emailError, messageError } = this.state;

    this.setState({ nameError: name ? false : true });
    this.setState({ messageError: message ? false : true });
    this.setState({ emailError: email ? false : true });

    if (name && email && message && !nameError && !emailError && !messageError) {
      this.setState({ formValid: true });
    } else {
      this.setState({ formValid: false });
    }
    e.preventDefault();
  }
  const { name, email, message, nameError, emailError, messageError, formValid } = this.state;
  if (!formValid) {
    return (
      <div style={styles.div}>
        <h1 style={styles.headingStyle}>contact</h1>
        <form className="form-floating needs-validation">
          <div className="form-floating mb-3">
            <input name="name" type="text" className="form-control" id="nameInput" placeholder="name" value={this.state.name} onChange={handleInputChange} onBlur={handleBlur} required></input>
            <label htmlFor="nameInput">name</label>
            {nameError
              ? <div className="alert alert-danger mt-2">Name is a required field.</div>
              : ''
            }
          </div>
          <div className="form-floating mb-3">
            <input name="email" type="email" className="form-control" id="emailInput" placeholder="name@example.com" value={email}  onChange={handleInputChange} required></input>
            <label htmlFor="emailInput" >email address</label>
            { emailError
            ? <div className="alert alert-danger mt-2">Email is a required field.</div>
            : ''
          }
          </div>
          <div className="form-floating">
            <textarea value={message} name="message" className="form-control" placeholder="Leave a comment here" id="messageInput" style={styles.textField} onChange={handleInputChange} required></textarea>
            <label htmlFor="messageInput">message</label>
          </div>
          <div className="m-3">
            <button className="btn" type="submit" onClick={handleFormSubmit} style={styles.submitBtn}>submit</button>
          </div>
        </form>
      </div>
    );
  };
};
