import React from 'react';
import '../../styles/Form.css';
import Alert from './components/Alert';

const styles = {
  div: {
    margin: "20px",
    padding: "10px",
  },
  headingStyle: {
    fontSize: '100px',
  },
  textField: {
    height: "100px",
  },
};

// Example starter JavaScript for disabling form submissions if there are invalid fields
(function () {
  'use strict'
  // Fetch all the forms we want to apply custom Bootstrap validation styles to
  var forms = document.querySelectorAll('.needs-validation')
  // Loop over them and prevent submission
  Array.prototype.slice.call(forms)
    .forEach(function (form) {
      form.addEventListener('submit', function (event) {
        if (!form.checkValidity()) {
          event.preventDefault()
          event.stopPropagation()
        }

        form.classList.add('was-validated')
      }, false)
    })
})()

export default function Contact() {
  return (
    <div style={styles.div}>
      <h1 style={styles.headingStyle}>contact</h1>
      <form class="form-floating needs-validation" novalidate>
        <div class="form-floating mb-3">
          <input type="text" class="form-control" id="floatingInput" placeholder="name" required></input>
          <label for="floatingInput">name</label>
          <div class="invalid-feedback">
            You must provide your name
          </div>
        </div>
        <div class="form-floating mb-3">
          <input type="email" class="form-control" id="floatingInputInvalid" placeholder="name@example.com" required></input>
          <label for="floatingInput">email address</label>
        </div>
        <div class="form-floating">
          <textarea class="form-control" placeholder="Leave a comment here" id="floatingTextarea2" style={styles.textField}></textarea>
          <label for="floatingTextarea2">message</label>
        </div>
        <div class="m-3">
          <button class="btn" type="submit" style={styles.submitBtn}>submit form</button>
        </div>
      </form>
    </div>
  );
}
